#include <stdio.h>

int main() {
	int N,number,a,b,c,repeat,different,most,max;  //样例个数（行数）N，序列数字个数number
	scanf("%d",&N);									//当前数字a，上一个数字b，上上个数字c
	for (int row = 0; row < N; ++ row) {			//重复数字个数repeat，不同数字个数different
		scanf("%d",&number);						//出现最多的最大数字max，它出现的次数most

		int occur[9999]={0};   //这五个数据是每次行循环都要重新赋值的，若放在最外面则最后要清零
		c=0;				   //其中第一个数字必定是不同数字，所以different=1
		repeat=0;				//数组occur[i]记录每个数字i的出现次数，以便最终输出max
		different=0;
		most=0;

		for (int i = 0; i < number; ++ i) {
			if(i==0){
				scanf("%d",&b);		//第一个数字没有上一个数字，
				occur[b]+=1;
				different+=1;
				continue;
			}
			scanf("%d",&a);
			occur[a]+=1;
			if(a!=b){
				different+=1;
			}
			if(a==b && a!=c){
				repeat+=1;
			}
			c=b;
			b=a;
		}
		for (int i = 1; i <= a ; ++ i) {
			if(occur[i]>=most){
				most=occur[i];
				max=i;
			}
		}
		printf("%d %d %d",different,repeat,max);
		putchar('\n');
	}
}

