	int x=num_words;
	for (int j = 0; j < x-1; ++ j) {
		for (int i = 0; i < x-1; ++ i) {	//朴素排序，n个元素用(n-1)^2次运算排序，两个循环，内外各排n-1次
			for (int n = 0; ; ) {
				if(*(words[i]+n) > *(words[i+1]+n) ){  //如果i指的字符大于i+1指的字符就交换它们储存的地址
					p=words[i+1];
					words[i+1]=words[i];
					words[i]=p;
					break;
				}
				else if(*(words[i]+n) == *(words[i+1]+n) ){  //如果字符相等就比较下一位
					++n;
				}
				else break;
			}
		}
	}
}

