#define bool int
bool isDigit(char c){
	if( c > '0' && c < '9')
		return 1;
	else 
		return 0;
}
bool isLower(char c){
	if( c >= 'a' && c <= 'z')
		return 1;
	else 
		return 0;
}
bool isUpper(char c){
	if( c >= 'A' && c <= 'Z')
		return 1;
	else 
		return 0;
}
bool isAlphabit(char c){
	if(isLower(c) || isUpper(c) )
		return 1;
	else 
		return 0;
}
bool isAlphabitOrNumber(char c){
	if(isAlphabit(c) ||isDigit(c) )
		return 1;
	else 
		return 0;
}
bool isSpace(char c) {
	if( c == (' '|| '\t'||'\r'||'\n'))
		return 1;
	else 
		return 0;
}
bool isPrintable(char c) {
	if(c >= '!' && c <= '~')
		return 1;
	else 
		return 0;
}
bool isGraphical(char c) {
	if(isPrintable(c) && !isSpace(c))
		return 1;
	else 
		return 0;
}
bool isPunctuation(char c) {
	if(isGraphical(c) && !isAlphabitOrNumber(c))
		return 1;
	else 
		return 0;}

