	}
	return ret;
}

int lcm(int a,int b){
	int ret=1;
	int lit;
	if(a>b) lit=a;
	else lit=b;
	for (int i = 1; i < lit/2; ++ i){
		if(a%i==0&&b%i==0) ret=i;
	}
