int getMaxCuttingPrice(int prices[][2], int priceCount, int rodLength){
	int price=0;
	int price1;
	if(rodLength<=priceCount){
		price=prices[rodLength - 1][1];
	}
	for (int i = 0; i <rodLength-1 ; ++ i) {
		int cut=i+1;
		 price1=getMaxCuttingPrice(prices, priceCount, cut)+getMaxCuttingPrice(prices, priceCount, rodLength-cut);
		if(price<price1){
			price=price1;
		}
	}
	return price;
}

