int gcd(int x,int y){
	int min=x;
	if(min>y){
		min=y;
	}
	int z;
	for (int i = min-1; i>0 ; -- i) {
		if(x%i==0&&y%i==0){
			z=i;
			break;
		}
	}
	return z;
}

int lcm(int x,int y){
	int max=x;
	int z;
	if(max<y){
		max=y;
	}
	for(int i=max+1;1;++i){
		if(i%x==0&&i%y==0){
			z=i;
			break;
		}
	}
	return z;
}

