		int pricecut=getMaxCuttingPrice( prices,  priceCount, rodLength-cut1)+getMaxCuttingPrice( prices, priceCount, cut1);
		maxprice1=(maxprice1>pricecut? maxprice1:pricecut);
	}
	return maxprice1;
}

