int getMultiplications(int n,int *dimensions){
	int i,j,k,l,tmp;
	for (i = 0; i <=n; ++ i) {
		dp[i][i]=0;
		memeo[i][i]=i;
	}
	for(l=2;l<=n;l++){//遍历长度从2到n 
		for(i=1;i<=n-l+1;i++){
			j=i+l-1;
			dp[i][j]=100000000;
			for(k=i;k<j;k++){//在k处截断，分成i~k，k+1~j，两部分 
				tmp=dp[i][k]+dp[k+1][j]+dimensions[i-1]*dimensions[k]*dimensions[j];
				if(tmp<dp[i][j]){
					dp[i][j]=tmp,memeo[i][j]=k; 
				}
			}
		}
	}
	return tmp;
}

