		if(num1 % i == 0 && num2 % i == 0){
			gcd = i;
		}
	}
	return gcd;
}

int isGcd(int num1, int num2){
	int gcd;
	for (int i = 1; i < num1 && i < num2; ++ i) {
		if(num1 % i == 0 && num2 % i == 0){
			gcd = i;
		}
	}
	return gcd;
}

int lcm(int num1, int num2){
	int result,gcd;
	gcd = isGcd(num1, num2);
	result = num1 * num2 / gcd;
	return result;
}

