int memory[100] = {0};
int knapsack(int prices[], int weights[], int maxWeight){
	if (maxWeight <= 0) 	return 0;
	if(memory[maxWeight]!=0)
		return memory[maxWeight];
	int maxPrice = 0;
	int a;
	for (int i = 0; i < 10; ++ i) {	
		maxPrice = (maxWeight==weights[i]?prices[i]:0);
	}
	int price = 0;
	for (int i = 0; i < 10; ++ i) {
		int cut = weights[i];
		a = max(knapsack(prices, weights,maxWeight-1),
				prices[i]+knapsack(prices, weights,maxWeight-cut));	
		if(a>price)  price = a;
	}
	maxPrice = max(maxPrice,price);
	memory[maxWeight] = maxPrice;
	return maxPrice;
	//A(Y) = max { A(Y - 1), max { pj + A(Y - wj) | wj ≤ Y } }
}

