int knapsack(int prices[], int weights[], int maxWeight){
	int A[11][maxWeight+1];
	for (int y = 1; y < maxWeight+1; ++ y) {	// 
		for (int j = 1; j < 11; ++ j){
			if(weights[j-1]>y){
				A[j][y] = A[j-1][y];
			}
			else{
				A[j][y] = max(A[j-1][y],prices[j-2]+A[j-1][y-weights[j]]);
			}
		} 
	}
	return A[10][maxWeight];

}
