void decToHex(const char dec[], char hex[]){
	int decnum = 0, //the number of the decimal array 
	   copynum = 0,//the copy of decnum  
	   n = 0,
	   hexdgt = 0;// calculate the digits of the heximal array 

	for (int i = 0; *dec ; ++ i, ++dec)//convert decimal character array to a number   
		decnum = 10 * decnum + *dec - '0';

	for ( hexdgt = 0, copynum = decnum; copynum ; ++hexdgt) 
		 copynum /= 16;// calculate the digits of the heximal array 

	for (int i = 0; i < hexdgt; ++ i, decnum /= 16, n = 0){ 
		  n = decnum % 16;
		if(n < 10)   hex[hexdgt - i - 1] = '0' + n;
		if(n >= 10)  hex[hexdgt - i - 1] = 'A'- 10 + n;
	} 
     hex[hexdgt] = 0;
}

void decToBin(const char dec[], char bin[]){
	int decnum = 0, //the number of the decimal array 
	   copynum = 0,//the copy of decnum  
	   n = 0,    // divide 0 or 1
	   bindgt = 0;// calculate the digits of the binary array 

	for (int i = 0; *dec ; ++ i, ++dec)//convert decimal character array to a number   
		decnum = 10 * decnum + *dec - '0';

	for ( bindgt = 0, copynum = decnum; copynum ; ++bindgt) 
		 copynum /= 2;// calculate the digits of the binary array 

	for (int i = 0; i < bindgt; ++ i, decnum /= 2, n = 0){ 
		  n = decnum % 2;
		  bin[bindgt - i - 1] = '0' + n;
	} 
     bin[bindgt] = 0;
}

