	f3 = 3 * 2 * 1;
	f5 = 5 * 4 * 3 * 2 * 1;
	f7 = 7 * 6 * 5 * 4 * 3 * 2 * 1;
	res = x - x3 / f3 + x5 / f5 - x7 / f7;
	return res;
}

