{
    int len1 = strlen(text1), len2 = strlen(text2);
    int dp[len1 + 1][len2 + 1];
    for (int i = 0; i <= len1; ++i)
    {
        for (int j = 0; j <= len2; ++j)
        {
            if (i == 0 || j == 0)
                dp[i][j] = 0;
