void insertionSort(int data[], int N)
{
    for (int i = 1; i <= N - 1; i++)
    {
        int j = i;
        while (j > 0 && data[j - 1] > data[j])
        {
            int temp = data[j];
            data[j] = data[j - 1];
            data[j - 1] = temp;
            j--;
        }
    }
}

