double sin(double x){
	double x3, f3;
	double x5, f5;
	double x7, f7;
	double res;
	x3 = x * x * x;
	x5 = x3 * x * x;
	x7 = x5 * x * x;
	f3 = 3 * 2;
	f5 = f3 * 4 * 5;
	f7 = f5 * 6 * 7;
	res = x - x3 / f3 + x5 / f5 - x7 / f7;
	return res;
}

