int gcd(int num1, int num2){
	int test;
	if(num1 < num2){
		test = num1;
		num1 = num2;
		num2 = test;
	}
	while(num1 % num2 != 0){
		test = num2;
		num2 = num1 % num2;
		num1 = test;
	}
	return num2;
}

int lcm(int num1, int num2){
	int test;
	if(num1 < num2){
		test = num1;
		num1 = num2;
		num2 = test;
	}
	int a = num1, b = num2;
	while(a % b != 0){
		test = b;
		b = a % b;
		a = test;
	}
	return num1 * num2 / b;
}

