_Bool isDigit(char c){
	int min = '0';
	int max = '9';
	return (c <= max && c >= min) ? 1 : 0;
} 
_Bool isLower(char c){
	int min = 'a';
	int max = 'z';
	return (c <= max && c >= min) ? 1 : 0;
}
_Bool isUpper(char c){
	int min = 'A';
	int max = 'Z';
	return (c <= max && c >= min) ? 1 : 0;
} 
_Bool isAlphabit(char c) {
	return(isUpper(c) == 1 || isLower(c) == 1) ? 1 : 0;
}
_Bool isAlphabitOrNumber(char c){
	return(isAlphabit(c) == 1 || isDigit(c) == 1) ? 1 : 0;
} 
_Bool isSpace(char c){
	return(c == ' ' || c == '\t' || c =='\r' || c == '\n') ? 1 : 0;
}//returns true if a character is one of ' ', '\t', '\r', or '\n'.
_Bool isPrintable(char c){
	int min = '!';
	int max = '~';
	return (c <= max && c >= min) ? 1 : 0;
}// returns true if a character is between '!' and '~'.
_Bool isGraphical(char c){
	return(isPrintable(c) == 1 && isSpace(c) == 0) ? 1 : 0;
}//returns true if a character isPrintable but not isSpace.
_Bool isPunctuation(char c) {
	return(isGraphical(c) == 1 && isAlphabitOrNumber(c) == 0) ? 1 : 0;
}//returns true if a character isGraphical but not isAlphabitOrNumber.

