int Max_occurrences(int nums[], int size, int *times){
	int num = 0;
	int time = 0;
	for (int i = 0; i < size; ++ i){
		for (int j = 0; j < size; ++ j){
			if(nums[i] == nums[j]){
				time ++;
			}
		} 
		if(time > *times){
			*times = time;
			num = nums[i];
		}
		time = 0;
	} 
	return num;
}

