	int symbol = (array[0] == '-') ? 1 : 0;
	int res = 0;
	for (int i = symbol; i < strlen(array); ++ i){
		res = res * 10 + array[i] - 48;
	} 
	return (symbol ? -res : res);
}

void itoa(int value, char array[]){
	int reverse = 0;
	int symbol = 0;
	if(value < 0){
		value = -value;
		symbol = 1;
		array[0] = '-';
	}

	while(value > 0){
		reverse = reverse * 10 + value % 10;
		value = value / 10;
	}
	for (int i = symbol; reverse > 0; ++ i){
		array[i] = reverse % 10 + 48;
		reverse /= 10;
	}
}

