void decToHex(const char dec[], char hex[]){
	for (int i = 0; i < 100; ++ i){
		hex[i] = '\0';
	} 
	int value = 0;
	for (int i = 0; dec[i] != 0; ++ i) {
		value = value * 10 + dec[i] - 48;
	}
	int record;
	int exam = value;
	for (int i = 0; exam > 0; ++ i){
		record = i;
		exam /= 16;
	} 

	for (int i = record; value > 0; -- i){
		if(value % 16 < 10){
			hex[i] = value % 16 + 48;
		}
		else{
			hex[i] = value % 16 + 55;
		}
		value /= 16;
	} 
}

void decToBin(const char dec[], char bin[]){
	for (int i = 0; i < 100; ++ i){
		bin[i] = '\0';
	} 
	int value = 0;
	for (int i = 0; dec[i] != 0; ++ i){
		value = value * 10 + dec[i] - 48;
	} 
	int record;
	int exam = value;
	for (int i = 0; exam > 0; ++ i){
		record = i;
		exam /= 2;
	} 

	for (int i = record; value > 0; -- i){
		bin[i] = value % 2 + 48;
		value /= 2;
	} 
}

