int getMaxCuttingPrice(int prices[][2], int priceCount, int rodLength){
	int price1 = 0;
	int price2;
	if(rodLength <= priceCount){
		price1 = prices[rodLength - 1][1];
	}
	for (int i = 1; i < rodLength; ++ i) {
		int length = i;
		price2 = getMaxCuttingPrice(prices, priceCount, length) + getMaxCuttingPrice(prices, priceCount, rodLength - length);
		if(price1 < price2){
			price1 = price2;
		}
	}
	return price1;
}

