int LCS(char *text1, char *text2){
	int res = 0;
	int i = strlen(text1);
	int j = strlen(text2);
   if(i == 0 || j == 0){
        return 0;
    }
	 else if(text1[i - 1] == text2[j - 1]){
        text1[i - 1] = 0;
        text2[j - 1] = 0;
        res = res + LCS(text1, text2) + 1;
    }
    else{
        res = res + compare( LCS(test(text1), text2), LCS(text1, test(text2)) );
    }
    return res;
}
