int bin[33];

int get_bits(int line, int lines)
{
    int repeat = (lines - 1) * 2;
    int result = 0;
    bin[line] = 1;
    if (line > 0)
    {
        bin[repeat - line] = 1;
    }
    for (int i = repeat; i < 32; i++)
    {
        bin[i] = bin[i % repeat];
    }
    for (int i = 0; i < 32; i++)
    {
        result = (result << 1) + bin[i];
    }

    for (int i = 0; i < 32; i++)
    {
        bin[i] = 0;
    }
    return result;
}

