int getMultiplications(int n,int *p){ 
  int i,j,t,k;   
  int r;             //记录相乘的矩阵个数变量
  int m[n][n];
  int s[n][n];
  for(i=1;i<=n;i++){ 
    m[i][i]=0;         //当一个矩阵相乘时，相乘次数为 0  
  }   
  //矩阵个数从两个开始一次递增  
  for(r=2;r<=n;r++){ 
    //从某个矩阵开始     
    for(i=1;i<=n-r+1;i++){ 
      //到某个矩阵的结束  
      j=i+r-1; 
      //拿到从 i 到 j 矩阵连乘的次数  
      m[i][j]=m[i+1][j]+p[i-1]*p[i]*p[j]; 
      //拿到矩阵连乘断开的位置  
      s[i][j]=i; 
      //寻找加括号不同，矩阵连乘次数的最小值，修改 m 数组，和断开的位置 s 数组  
      for(k=i+1;k<j;k++){ 
        t=m[i][k]+m[k+1][j]+p[i-1]*p[k]*p[j]; 
        if(t<m[i][j]){ 
          m[i][j]=t; 
          s[i][j]=k; 
        } 
      } 
    } 
  }
  return m[1][9];
}

