int astrcmp(char *words[], int x){
	int i = 0;
	char *a = words[x];
	char *b = words[x+1];
	while(*a == *b && *a !=' '&& *b != ' '){
		++a;
		++b;
	}
	return (*b - *a);
}

void sort_words(char *words[], int num_words){
	for (int i = 0; i < num_words - 1; ++ i){
		for (int j = 0; j < num_words - 1; ++ j){
			if(astrcmp(words,j) < 0){
				char *c = 0;
				c = words[j];
				words[j] = words[j+1];
				words[j+1] = c;
			}
		} 
	} 
}

