int gcd(int num1,int num2 ){
	int min=(num1<num2?num1:num2);
	int rslt;
	for (int i = min; i >0 ;-- i) {
		if(num1%i==0&&num2%i==0){
			rslt=i;
			break;
		}

	}
	return rslt;
}

int lcm(int num1,int num2){
	int min=(num1<num2?num1:num2);
	int gcd,rslt;
	for (int i = min; i >0 ;-- i) {
		if(num1%i==0&&num2%i==0){
			gcd=i;
			break;
		}
	}
	rslt=(num1*num2)/gcd;
	return rslt;
}

