int reverse(int val){
	int number[10];
	for (int i = 0; i < 10; ++ i){
		number[i]=0;
	} 
	int x=val;
	int n=0;
	for (int j = 1;x>0; ++ j) {
		number[j]=x%10;
		x=x/10;
		n++;
	}
	int sum=0;
	for (int k = n; k > 0;k--){
		int t=number[k];
		for (int m = 0; m < n-k; ++ m) {
			t*=10;
		}
		sum+=t;
	} 
	return sum;
}

