}

int lcm(int num1, int num2){
	int min,gcd=1;
	if(num1<num2){
		min=num1;
	}else{
		min=num2;
	}
	for (int i = 2; i <= min; ++ i) {
		if(num1%i==0&&num2%i==0){
			gcd=i;
		}
	}
	int lcm;
	lcm=num1 * num2 / gcd;
	return lcm;
}

