} A[i]= max(A[i - 1],t);
}return A[maxWeight];
}

int max(int a,int b){
	if(a>b) return a;
	else return b;
}
int knapsack(int prices[],int weights[],int maxWeight){
	int A[100][100]={0};
for (int i = 0; i <=maxWeight ; ++ i){
	for (int j = 1; j <10; ++ j){
		if(weights[j]>i) A[j][i]=A[j-1][i];
		if(weights[j]<=i)A[j][i]=max(A[j - 1][i],prices[j] + A[j - 1][i-weights[j]]);
} 
} return A[9][maxWeight];	
}
