#include "source.c"
#include <stdio.h>

void read(int array[], int length) {
	for (int i = 0; i < length;  ++ i) {
		scanf("%d", &array[i]);
	}
}

int main() {
	int prices[10];
	read(prices, 10);
	int weights[10];
	read(weights, 10);
	int maxWeight;
	scanf("%d", &maxWeight);

	printf("%d", knapsack(prices, weights, maxWeight)); 
}

int max(int a, int b){
	if(a > b)
		return a;
	return b;
}

int function(int weights[], int prices[], int maxWeight,int j){
	int sum = 0;

	if(j == 1){
		if(weights[j - 1] <= maxWeight)
			sum += prices[0];
		else	
			sum = 0;
	}

	else {	//j>=2
		if (weights[j - 1] <= maxWeight)
			sum += max(function(weights, prices, maxWeight - weights[j - 1], j - 1) + prices[j - 1],function(weights, prices, maxWeight, j - 1))  ;
	else 
			sum += function(weights, prices, maxWeight, j - 1);
	}

	return sum;
}

int knapsack(int prices[], int weights[], int maxWeight){

	return function(weights, prices, maxWeight, 10);
}
