int reverse(int a){
	int n = a;
	int count = 0;
	int sum = 0;
	while( n != 0 ){
		n = n / 10;
		++ count;
	}
	for(int i = 0; i < count; ++i){
		sum = a % 10 + sum * 10;
		a = a / 10;
	}
	return sum;
}

