int getMultiplications(int n, int *dimensions){
	int ans = 100000;
	int i, ans1;
	if(n == 1)
		return 0;
	for (i = 1; i < n; ++ i){
		int * p = &dimensions[i];
		ans1 = getMultiplications(n - i , p) + getMultiplications(i , dimensions) + ((dimensions[0])*(*(p))*(dimensions[n]));
		if(ans1 < ans)
			ans = ans1;
	} 
	return ans;
}
