	scanf("%d",&n);
	for(i=1;i<=n/2;++i){
		for(j=1;j<=n;++j){
		if(i==j){printf("\\");}
		else if(j<i||(i+j)>n+1){printf("*");}
		else if((i+j)==n+1){printf("/");}
		else if(j>i&&(i+j)<n+1){printf("+");}
		}
		printf("\n");
	}
	for(i=n/2+1;i<=n;++i){
		for(j=1;j<=n;++j){
		if(i==j){printf("\\");}
