_Bool isAlphabit(char c){
	if(isLower(c)||isUpper(c)){
		return 1;
	}
	else{
		return 0;
	}
}// returns true if a character isLower or isUpper.
_Bool isAlphabitOrNumber(char c){
	if(isAlphabit(c)||isDigit(c)){
		return 1;
	}
