		return 0;
	}
} //returns true if a character isAlphabit or isDigit.
_Bool isSpace(char c){
	int a,b,e,d;
	a=(int)' ';
	b=(int)'\t';
	e=(int)'\r';
	d=(int)'\n';
	if(c==b||c==a||c==e||c==d){
		return 1;
	}
	else{
		return 0;
	}
}// returns true if a character is one of ' ', '\t', '\r', or '\n'.
_Bool isPrintable(char c){
	int a,b;
	a=(int)'!';
	b=(int)'~';
	if(c<=b&&c>=a){
		return 1;
	}
	else{
		return 0;
	}
}// returns true if a character is between '!' and '~'.
_Bool isGraphical(char c){
	if(isPrintable(c)&&!isSpace(c)){
		return 1;
	}
	else{
		return 0;
	}
}// returns true if a character isPrintable but not isSpace.
_Bool isPunctuation(char c){
