	return str;
}

int knapsack(int prices[],int weights[],int maxWeight){
	double per[10]={0};
	double n=0;
	int num=0;
	if(maxWeight==0){
		return 0;
	}
	for (int i = 0; i < 10; ++ i){
		per[i]=prices[i]*1.0/weights[i];
	} 
	for (int i = 0; i < 10; ++ i){
		if(per[i]>n){
			n=per[i];
			num=i;
		}
	} 
	int cou=maxWeight/weights[num];
	int max=cou*prices[num];
	int left=maxWeight-cou*weights[num];
	for (int i = 0; i < 10; ++ i) {
		if(left>=weights[i]){
			max+=prices[i];
			left-=weights[i];
		}
	}
	return max;
}
