double sin(double x){
	int sign = -1;
	double r = x;
	double t = x;
	for (int i = 0; i < 10; + + i) {
		t = t * x * x / (i * 2 + 2 )/(i * 2 + 3);
		if (t < 1e-8) break;
		r += sign * t;
		sign * = -1;
	}
	return r;
}
