int knapsack(int prices[],int  weights[],int  maxWeight){
	int num=0;
	int total[100]={0};
	int flag=1;
	for (int i = 0; i < 10; ++ i){
		if(maxWeight>weights[i]){
			total[num++]=prices[i]+knapsack(prices,weights,maxWeight-weights[i]);
			flag=0;
		}
	} 
	if(flag==1){
		return 0;
	}
	return max(total,num);
}
