int LCS(char text1[],char text2[]){
	int dp[100][100];
	int m=strlen(text1),n=strlen(text2);
	for (int i = 1; i <= m; ++ i)
	for (int j = 1; j <= n; ++ j){
		dp[i][j]=Max(dp[i-1][j],dp[i][j-1]);
		if(text1[i-1]==text2[j-1]) dp[i][j]=Max(dp[i-1][j-1]+1,dp[i][j]);
	}
	return dp[m][n];
}
