#include<stdio.h>

 int differ(int arry[],int n){
    int k=1;//k would count the different numbers
    for(int i=1;i<n;i++){
        if(arry[i]!=arry[i-1]){
          k++;
        }
     }
        return k;
}


 int repeat(int arry[],int n){
    int k=0,//k would count the numbers that repeat in a number group 
       num=0;//num would count the repeat-times of a number  

    for(int i=0;i<n;i++){

      for(int j=i+1;j<n;j++){
        if(arry[j]==arry[i]){
          num++;
          }
        else{
          break;
          }
      }

      if(num!=0){
         k++;
         i+=num;
         num=0;
        }
    }
        return k;
 }

int bgrepeat(int arry[],int n){
    int num=0,//num would count the repeat-times of a number  
        rptimes=0,/*rptimes would record 
		           the largest repeat-times*/ 
        rpnumber=arry[0];/*rpnumber would record the number
		               that has the largest repeat-times */

    for(int i=0;i<n;i++){
      for(int j=i+1;j<n;j++){
        if(arry[j]==arry[i]){
          num++;
          }
        else{
          break;          
          }
      }  
        if(num>=rptimes){
            rptimes=num,
            rpnumber=arry[i];
          }
         i+=num;
         num=0;
    }
        return rpnumber;
}


