}

int largest (int a,int b,int c){
	int x;
	if(a>=b&&a>=c) x=a;
	if(b>=a&&b>=c) x=b;
	if(c>=a&&c>=b) x=c;
	return x;
}

int gcd (int a,int b){
	int x,y;
	if(a<=b) x=a;
	else x=b;
	for (int i = 1; i <= x; ++ i){
  if(a%i==0&&b%i==0)  y=i;     
	} 
	return y;
}

int lcm (int a,int b){
	int x,y;
	if(a<=b) x=a;
	else x=b;
	for (int i = 1; i <= x; ++ i){
  if(a%i==0&&b%i==0)  y=i;     
	} 
	return a*b/y;
}

