int get_bits(int i,int x){
    int result = 0;
    int bin[32];
    int y = 32 / (2 * x - 2);
    int z = 32 - y;
    for (int a = 0; a < y;a++){
        for (int b = 0; b < i;b++){
            bin[b] = 0;
        }
        bin[a * (2 * x - 2) + i] = 1;
        for (int c = 1; c <= 2 * x - 2 * i + 1;c++){
            bin[a * (2 * x - 2) + i + c] = 0;
        }
        bin[a * (2 * x - 2) + i + 2 * x - 2 * i + 1] = 1;
        for (int d = 1; d <= i - 1;d++){
            bin[a * (2 * x - 2) + i + 2 * x - 2 * i + 1 + d] = 0;
        }
    }
    for (int e = 1; e <= z;e++){
        if(e==i+1||e==2*x-i-1)
            bin[y * (2 * x - 2) + e] = 1;
            else
                bin[y * (2 * x - 2) + e] = 0;
    }
    for (int f = 0; f < 32;f++){
        result = (result << 1) + bin[i];
    }
    for (int i = 0; i < 32; i++)
    {
        bin[i] = 0;
    }
    return result;
}

