#include "source.c"
#include <stdio.h>

int main() {
	int dimensions[10];
	for (int i = 0; i < 10; ++ i) {
		scanf("%d", &dimensions[i]);
	}
	printf("%d", getMultiplications(9, dimensions));
}

int min(int a,int b)
{
	return a<b?a:b;
}
int ans(int i,int j,int dimensions[])
{
	int Min=0x7FFFFFFF;
	if(i==j)
	  return 0;
	if(i+1==j)
	  return dimensions[i-1]*dimensions[i]*dimensions[j];
	for(int k=i;k<j;k++)
	  Min=min(Min,(ans(i,k,dimensions)+ans(k+1,j,dimensions)+dimensions[i-1]*dimensions[k]*dimensions[j]));
	return Min;
}
int getMultiplications(int n,int dimensions[])
{
	return ans(1,n,dimensions);
}
