{
	int bit=0;
	int a[10]={0};
	while(x!=0)
	{
		a[++bit]=x%10;
		x/=10;
	}
	int i;
	for(i=1;i<=bit/2;i++)
	  if(a[i]!=a[bit-i+1])
	    break;
	if(i==(bit/2+1))
	  return 1;
	else
	  return 0;
}
