int P[15][15];//P[i][j]为从矩阵i乘到矩阵j在哪儿划分最好
int M[19][19];

//算法3.6最小量乘法
int getMultiplications(int n,const int d[])//n为几个矩阵相乘，d为维度，
{
    int i,j,k,diagonal;
    for(i=1;i<=n;i++)
        M[i][i]=0;
    for(diagonal=1;diagonal<=n-1;diagonal++)//对角线1刚刚高于主对角线
    {
        for(i=1;i<=n-diagonal;i++)
        {
            j=i+diagonal;
            M[i][j]=M[i][i]+M[i+1][j]+d[i-1]*d[i]*d[j];//首先给M一个初始值，在下面求min的时候也好有个比较。
            P[i][j]=i;//同样需要提前赋值，不然下面if不成立的话，P[i][j]就空了。
            for(k=i+1;k<=j-1;k++)
            {
                int temp=M[i][k]+M[k+1][j]+d[i-1]*d[k]*d[j];
                if(temp<M[i][j])
                {
                    M[i][j]=temp;
                    P[i][j]=k;
                }
            }
        }
    }
    return M[1][n];
}

