		}
	}
	return 1;
}

int emirp(int x){ //对x取反的函数
	int b = 0;
	int c;
	while (x != 0){
	if (x / 10 != 0){
		c = x % 10;
		b = b * 10 + c;
		x = x / 10;
	}
	else {
		b = b * 10 + x;
		x = x / 10;
	}
	}
	return b;
}

