		num = num * 10 + (dec[i] - '0');
	} 
	int count ;
	int s[100];
	for (count = 0; num != 0 ; ++ count){
		s[count] = num % 2;
		num = num - num % 2;
		num = num / 2;
	} 
	count = count - 1;
	int x = count;
	int j;
	for (j = 0; j <= x; ++ j){
		bin[j] = s[count] + '0';
		count --;
	} 
	bin[j] = '\0';
}

