	int j=0,subc,q;
	struct Result result;
	for(int i=0;i<strlen(text1);++i){
		for(int k=0;k<strlen(text2);++k){
			if(text1[i]==text2[k]){
				result.LCS[j]=text2[k];
				j=j+1;
				subc=k+1;
				q=j;
				for(int w=0;w<subc;++w){
					text2[w]='a';
				}
				break;
			}
		}
	}
	result.LCS[q]='\0';
	return result;
}

#define MIN(x,y) (x<y?x:y)

int fakegetMultiplications(int i,int j,int dim[]){
	int min_opt,opt=99999999;
	if(i==j) {
		return 0;
	}//分割为第一个矩阵和后面一坨的特殊情况
	for(int k=i;k<j;++k){
		min_opt=fakegetMultiplications(i,k,dim)+fakegetMultiplications(k+1,j,dim)+dim[i-1]*dim[k]*dim[j];
		/*1.上面那个乘法的最后一步是第一项(i-1)乘最后一项(j)再乘分割的那一项(k)*/
		opt=MIN(min_opt,opt);
	}
	return opt;
}//递归函数的时候不要仔细算小分支里的东西(但是要检查是否存在问题)，弄清楚最大的那一块才是好办法

int getMultiplications(int x,int dims[]){
	return fakegetMultiplications(1,x,dims);
}

int swap(int *x,int *y){
	int temp=*x;
	*x=*y;
	*y=temp;
}

int insertionSort(int data[],int x){
	for(int i=0;i<x-1;++i){
		for(int k=0;k<x-1;++k){
			if(data[k]>data[k+1]){
				swap(&data[k],&data[k+1]);
			}
		}
	}
}

#define MAX(x,y) (x>y?x:y)

int book[10]={0};
int knapsack(int prices[],int weights[],int maxweight){
	int max=0;
	for(int i=0;i<10;++i){
		if(maxweight>=weights[i]&&book[i]==0){
			book[i]=1;
		    max=MAX(max,prices[i]+knapsack(prices,weights,maxweight-weights[i]));
			book[i]=0;
	}
}
	return max;
}

