int max_price_memory[1000]={0};

int getMaxCuttingPrice(int prices[][2],int priceCount,int rodlength){
	if(max_price_memory[rodlength]>0) {
		return max_price_memory[rodlength];
	}
	int cur_max_price=(rodlength<=6?(prices[rodlength-1][1]):0);
	for(int i=0;i<rodlength-1;++i){
		int first_cut=i+1;
		int price=getMaxCuttingPrice(prices,6,first_cut)+getMaxCuttingPrice(prices,6,rodlength-first_cut);
		cur_max_price=MAX(price,cur_max_price);
	}
	max_price_memory[rodlength]=cur_max_price;
	return cur_max_price;
}
